import { useNavigate } from "react-router-dom";
import { Modal } from "react-bootstrap";
import { useState } from "react";

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Design Pattern: CONTROLLER 
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h4>CONTROLLER</h4>
        <p>
        Single handler design pattern â€¦ authentication
        <br/>
        The front controller design pattern is used to provide a centralized request handling mechanism so that all requests will be handled by a single handler. 
        This handler can do the <b>authentication/ authorization/ logging</b> or tracking of request and then pass the requests to corresponding handlers.        </p>
      </Modal.Body>
      <Modal.Footer>
        <Link to="/" onClick={props.onHide}>Close</Link>
      </Modal.Footer>
    </Modal>
  );
}



const SelectAdd = () => {
  const navigate = useNavigate();
  const [modalShow, setModalShow] = useState(false);
  return (
    <>
      <input
          type="submit"
          value="Click Me"
          name="info"
          onClick={() => setModalShow(true)}
        />
        <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />

      <ul class="nav navbar flex-column">
        <li class="nav-item p-2">
          <button
            onClick={() => {
              navigate("/addstudent");
            }}
            class="btn  btn-outline-dark mt-5"
            aria-current="page"
            href="#"
          >
            Add Student
          </button>
        </li>
        <li class="nav-item p-2">
          <button
            onClick={() => {
              navigate("/addteacher");
            }}
            class="btn btn-outline-dark mt-3 "
            aria-current="page"
            href="#"
          >
            Add Teacher
          </button>
        </li>
        <li class="nav-item p-2">
          <button
            onClick={() => {
              navigate("/addstaff");
            }}
            class="btn btn-outline-dark mt-3"
            aria-current="page"
            href="#"
          >
            Add Staff
          </button>
        </li>
      </ul>
    </>
  );
};

export default SelectAdd;
